import React, { useEffect, useState } from "react";
import './Home.css';
import Header from "../../common/header/Header";

import {
  GridList,
  GridListTile,
  GridListTileBar,
  Box,
  Card,
  CardContent,
  Typography,
  CardActions,
  Button,
  FormControl,
  TextField,
  Select,
  MenuItem,
  ListItemIcon,
  ListItemText,
  Checkbox,
  InputLabel,
} from "@material-ui/core";

import { 
  Theme, 
  createStyles, 
  makeStyles,
} from "@material-ui/core/styles";

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      display: "flex",
      flexWrap: "wrap",
      
      "& > *": {
        maxWidth: 240,
        minWidth: 240,
        margin: theme.spacing.unit,
      },
    },
    titleColor: {
      color: theme.palette.primary.light
    },
    formControl: {
      margin: theme.spacing(1),
      width: 300
    },
    indeterminateColor: {
      color: "#f50057"
    },
    selectAllText: {
      fontWeight: 500
    },
    selectedAll: {
      backgroundColor: "rgba(0, 0, 0, 0.08)",
      "&:hover": {
        backgroundColor: "rgba(0, 0, 0, 0.08)"
      }
    },
  })
);

const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;
const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: 250
    }
  },
  getContentAnchorEl: null,
  anchorOrigin: {
    vertical: "bottom",
    horizontal: "center"
  },
  transformOrigin: {
    vertical: "top",
    horizontal: "center"
  },
  variant: "menu"
};

function Home(props){

  const [upComingMovie, setUpComingMovie] = useState([]);
  const [movieDetails, setMovieDetails] = useState([]);
  const [selected, setSelected] = useState([]);
  const [genreOptions, setGenreOptions] = useState([]);

  const classes = useStyles();

  useEffect(() => {
    let dataShows = null;

    fetch(props.baseUrl + "movies/?limit=17", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        "Cache-Control": "no-cache",
      },
    })
      .then((response) => response.json())
      .then((response) => {
        //console.log(response.movies);
        let upComingArr = [];
        let movieDeetsArr = [];
        let count = 0;
        for(let data of response.movies) {
          upComingArr.push({
            id: count,
            title: data.title,
            poster_url: data.poster_url
          });
          movieDeetsArr.push({
            id: count,
            title: data.title,
            poster_url: data.poster_url
          });
          count = count + 1;
        }

        console.log(upComingArr);
        setUpComingMovie(upComingArr);
        setMovieDetails(movieDeetsArr);
      });
      fetch(props.baseUrl + "genres", {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
          "Cache-Control": "no-cache",
        }
      })
        .then((response) => response.json())
        .then((response) => {
          let genreOptions = [];
          for(let data of response.genres){
            genreOptions.push(data.genre);
          }
          console.log(genreOptions);
          setGenreOptions(genreOptions);
        })
  }, []);

  const isAllSelected =
    genreOptions.length > 0 && selected.length === genreOptions.length;

  const handleChange = (event) => {
    const value = event.target.value;
    if (value[value.length - 1] === "all") {
      setSelected(selected.length === genreOptions.length ? [] : genreOptions);
      return;
    }
    setSelected(value);
  };

  const mainBody = (
        <div>
            <Header />
            
            <div className="upComingMovies">
                Upcoming Movies
            </div>


            <div>
              <GridList cellHeight={250} cols={6} style={{ flexWrap: 'nowrap', transform: 'translateZ(0)' }}>
                {upComingMovie.map((data) => (
                  <GridListTile key={data.id}>
                    <img src={data.poster_url} alt={data.title} />
                    <GridListTileBar title={data.title}/>
                  </GridListTile>
                ))}
              </GridList>
            </div>


            <div className="mainbody">
              <div className="movieTab">
                <GridList cellHeight={350} cols={4}>
                  {movieDetails.map((data) => (
                    <GridListTile key={data.id} style={{cursor: 'pointer'}}>
                      <img src={data.poster_url} alt={data.title} />
                      <GridListTileBar title={data.title}/>
                  </GridListTile>
                ))}
              </GridList>
              </div>


              <div className="filterTab">
                <Box className={classes.root}>
                  <Card>
                    <CardContent>
                      <Typography className={classes.titleColor} variant='string'>FIND MOVIES BY:</Typography>
                      <FormControl>
                        <TextField id="standard-basic" label="Movie Name" variant="standard" />

                        
                        <InputLabel id="mutiple-select-label-genre">Genre</InputLabel>
                        <Select
                          labelId="mutiple-select-label-genre"
                          multiple
                          value={selected}
                          onChange={handleChange}
                          renderValue={(selected) => selected.join(", ")}
                          MenuProps={MenuProps}
                        >
                          <MenuItem
                            value="all"
                            classes={{
                              root: isAllSelected ? classes.selectedAll : ""
                            }}
                          >
                            <ListItemIcon>
                              <Checkbox
                                classes={{ indeterminate: classes.indeterminateColor }}
                                checked={isAllSelected}
                                indeterminate={
                                  selected.length > 0 && selected.length < options.length
                                }
                              />
                            </ListItemIcon>
                              <ListItemText
                                classes={{ primary: classes.selectAllText }}
                                primary="Select All"
                              />
                          </MenuItem>
                          {genreOptions.map((genreOptions) => (
                            <MenuItem key={genreOptions.id} value={genreOptions.genre}>
                              <ListItemIcon>
                                <Checkbox checked={selected.indexOf(genreOptions) > -1} />
                              </ListItemIcon>
                              <ListItemText primary={genreOptions.genre} />
                            </MenuItem>
                          ))}
                        </Select>


                        <TextField id="standard-basic" label="Artist" variant="standard" />
                      </FormControl>
                    </CardContent>
                    <CardActions>
                      <Button variant="contained" color='primary'>Apply</Button>
                    </CardActions>
                  </Card>
                </Box>
              </div>
            </div>
        </div>
    );

  return mainBody;
}

export default Home;
