import React, { useEffect, useState } from 'react';
import ReactDOM from 'react-dom';
import './Details.css';
import Header from "../../common/header/Header.js";
import { 
    Typography,
    GridList,
    GridListTile,
    GridListTileBar,
}from "@material-ui/core";
import { Link } from "react-router-dom";

function Details(props){

    const [movieInfo, setMovieInfo] = useState([]);

    useEffect(() => {
        fetch(props.baseUrl + "movies/" + props.match.params.id, {
            method: "GET",
            headers: {
              "Content-Type": "application/json",
              "Cache-Control": "no-cache",
            }
          })
            .then((response) => response.json())
            .then((response) => {
                let movieInfomer = [];
                movieInfo.push(response);
                console.log(response.poster_url);
                setMovieInfo(movieInfomer);
            })
    },[]);

    const mainBody = (
        <div>
            <Header />
                <div className="backtohome">
                    <Link style={{textDecoration: 'none'}} to={"/"}>
                        <Typography className="back">
                            &lt; Back to Home
                        </Typography>
                    </Link>
                </div>
                <div className="mainbody">
                    <div className="picturetab" >
                        <GridList cellHeight={250} cols={1} style={{ flexWrap: 'nowrap'}}>
                            <GridListTile><img src={movieInfo.poster_url} alt={movieInfo.title} /></GridListTile>
                        </GridList>
                    </div>
                    <div className="maintab">
                        w
                    </div>
                    <div className="actortab">
                        q
                    </div>
                </div>
        </div>
    );
    return mainBody;
}

export default Details;
