import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import GridListTileBar from '@material-ui/core/GridListTileBar';

const styles = theme => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'space-around',
    overflow: 'hidden',
    backgroundColor: theme.palette.background.paper,
  },
  gridList: {
    flexWrap: 'nowrap',
    // Promote the list into his own layer on Chrome. This cost memory but helps keeping high FPS.
    transform: 'translateZ(0)',
  },
  title: {
    color: theme.palette.primary.light,
  },
  titleBar: {
    background:
      'linear-gradient(to top, rgba(0,0,0,0.7) 0%, rgba(0,0,0,0.3) 70%, rgba(0,0,0,0) 100%)',
  },
});

function upComingMoviesGridList(props) {
  const { classes } = props;

  const upComingMoviesList = [
    {
      "poster_url":"../../assets/UpcommingMovies/OnePieceRed.jpg",
      "upComingMovieName":"One Piece: Film Red"
    },
    {
      "poster_url":"../../assets/UpcommingMovies/wakandaforever2.jpg",
      "upComingMovieName":"Wakanda Forever"
    },
    {
      "poster_url":"../../assets/UpcommingMovies/missionImpossible.jpg",
      "upComingMovieName":"Mission:Impossible Dead Reckoning"
    },
    {
      "poster_url":"../../assets/UpcommingMovies/avatar2.jpg",
      "upComingMovieName":"Avatar 2"
    },
    {
      "poster_url":"",
      "upComingMovieName":"place_holder"
    },
    {
      "poster_url":"",
      "upComingMovieName":"place_holder"
    }
  ];

  return (
    <div className={classes.root}>
      <GridList className={classes.gridList} cols={2.5}>
        {upComingMoviesList.map(tile => (
          <GridListTile key={tile.poster_url}>
            <img src={tile.poster_url} alt={tile.upComingMovieName} />
            <GridListTileBar
              title={tile.upComingMoviesList}
              classes={{
                root: classes.titleBar,
                title: classes.title,
              }}
            />
          </GridListTile>
        ))}
      </GridList>
    </div>
  );
}

upComingMoviesGridList.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(upComingMoviesGridList);
